import React, { useState, useEffect } from "react";

const Blog = () => {
    const [blogs, setBlogs] = useState([]);

    const showblog = async () => {
        try {
            const responseApi = await fetch(`http://localhost:8000/api/show-blog`);
            const fetchData = await responseApi.json();
            setBlogs(fetchData.data || []); // Adjust for API response structure
        } catch (error) {
            console.error("Error fetching blogs:", error);
        }
    };

    useEffect(() => {
        showblog();
    }, []);

    return (
        <div className="main-blogs">
            {Array.isArray(blogs) && blogs.map((blogItem, i) => (
                <div className="blog-item-inner" key={i}>
                    <h2>{blogItem.title}</h2>
                </div>
            ))}
        </div>
    );
};

export default Blog;
