import React, { useState } from "react";
import { Link } from 'react-router-dom';
import '.assets/nav.css'; // Import the CSS file

const Nav = () => {
  const [isMobile, setIsMobile] = useState(false);
  const [activeIndex, setActiveIndex] = useState(null); // State for the accordion toggle

  const toggleMobileMenu = () => setIsMobile(!isMobile);

  const handleAccordionClick = (index) => {
    // Toggle the clicked index to open/close the item
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div className="main-menu">
      <div className="logo">My Logo</div>
      <div className={`menu-items ${isMobile ? 'active' : ''}`}>
        <ul>
          <li className={activeIndex === 0 ? 'active' : ''} onClick={() => handleAccordionClick(0)}>
            <Link to="/">Home</Link>
          </li>
          <li className={activeIndex === 1 ? 'active' : ''} onClick={() => handleAccordionClick(1)}>
            <Link to="/about">About</Link>
          </li>
          <li className={activeIndex === 2 ? 'active' : ''} onClick={() => handleAccordionClick(2)}>
            <Link to="/contact">Contact Us</Link>
          </li>
          <li className={activeIndex === 3 ? 'active' : ''} onClick={() => handleAccordionClick(3)}>
            <Link to="/portfolio">Portfolio</Link>
          </li>
        </ul>
      </div>
      <div className="hamburger" onClick={toggleMobileMenu}>
        <div className="bar"></div>
        <div className="bar"></div>
        <div className="bar"></div>
      </div>
    </div>
  );
};

export default Nav;
