import React from 'react';

// Dynamically import all images from the 'images' folder
const images = require.context('./assets/images', false, /\.(png|jpe?g|svg)$/);

const Portfolio = () => {
  const projects = [
    {
      title: 'Tenperformance',
      description: 'This is a description of the project.',
      link: 'https://www.tenperformance.com',
      projectImageKey: 'tenperformance.png', // The image filename key
    },
    {
      title: 'Jessakae',
      description: 'This is a description of the project.',
      link: 'https://jessakae.com',
      projectImageKey: 'jessakae.png',
    },
      {
      title: 'Avaestell',
      description: 'This is a description of the project.',
      link: 'https://avaestell.com',
      projectImageKey: 'avaestell.png',
    },
    {
      title: 'Jm Distro',
      description: 'This is a description of the project.',
      link: 'https://jm-distro.com/',
      projectImageKey: 'jm-distro.png',
    },
    {
      title: 'Wipstitch',
      description: 'This is a description of the project.',
      link: 'https://wipstitch.com/',
      projectImageKey: 'wipstitch1.png',
    },

  ];

  return (
    <div className="portfolio">
      <header>
        <h1>My Portfolio</h1>
        <p>Welcome to my portfolio! I am a web developer specializing in Shopify Plus.</p>
      </header>
      <section className="projects">
        <h2>Projects</h2>
        <div className="project-list">
          {projects.map((project, index) => {
            // Get the image by the key
            const imageSrc = images(`./${project.projectImageKey}`);
            return (
              <div key={index} className="project">
                {imageSrc ? (
                  <img
                    src={imageSrc}
                    alt={`${project.title} Screenshot`}
                    className="project-image"
                  />
                ) : (
                  <div className="image-placeholder">
                    <p>Image not available</p>
                  </div>
                )}
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <a href={project.link} target="_blank" rel="noopener noreferrer">
                  View Project
                </a>
              </div>
            );
          })}
        </div>
      </section>
      <footer>
        <h2>Contact Me</h2>
        <p>Email: sandeepptpss@gmail.com</p>
      </footer>
    </div>
  );
};

export default Portfolio;
