import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";

const PortfolioDetails = () => {
  const [details, setDetails] = useState({});
  const [data, setData] = useState([]);
  console.log(data)
  const { id } = useParams();

  // Fetch details for the specific portfolio item
  const fetchData = useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:8000/api/getportfolio/${id}`);
      if (!response.ok) throw new Error("Failed to fetch portfolio details.");
      const result = await response.json();
      setDetails(result);
    } catch (error) {
      console.error("Error fetching portfolio details:", error);
    }
  }, [id]);

  // Fetch all portfolio items
  const fetchData1 = useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:8000/api/portfolioview/`);
      if (!response.ok) throw new Error("Failed to fetch portfolio list.");
      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error("Error fetching portfolio list:", error);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  useEffect(() => {
    fetchData1();
  }, [fetchData1]);

  return (
    <>
      <div className="main-product-detail">
        <div className="main-inner-side">
          <div className="left-side-data">
            {details.image && (
              <img
                src={`http://localhost:8000/${details.image}`}
                width="500px"
                height="450px"
                alt={details.title || "Product"}
              />
            )}
          </div>
          <div className="right-side-data">
            <h2>{details.title}</h2>
          </div>
        </div>
        <p className="product-description">{details.description}</p>
      </div>
    </>
  );
};

export default PortfolioDetails;
